import React, { useState, useEffect, useRef } from 'react';
import './NextviewportMenu.css';

const NextviewportMenu = () => {
    const [activeSection, setActiveSection] = useState('section1');
    const sectionRefs = useRef({});

    useEffect(() => {
        const handleScroll = () => {
            let currentSectionId = null;
            Object.keys(sectionRefs.current).forEach(sectionId => {
                const element = sectionRefs.current[sectionId];
                if (element) {
                    const rect = element.getBoundingClientRect();
                    if (
                        rect.top <= window.innerHeight / 2 &&
                        rect.bottom >= window.innerHeight / 2
                    ) {
                        currentSectionId = sectionId;
                    }
                }
            });

            if (currentSectionId && currentSectionId !== activeSection) {
                setActiveSection(currentSectionId);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [activeSection]);

    const handleScrollToSection = (sectionId) => {
        const element = sectionRefs.current[sectionId];
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
            setActiveSection(sectionId);
        }
    };

    return (
        <div>
            <ul className="next-viewport-menu">
                <li onClick={() => handleScrollToSection('section1')}
                    className={activeSection === 'section1' ? 'active' : ''}>Section 1</li>
                <li onClick={() => handleScrollToSection('section2')}
                    className={activeSection === 'section2' ? 'active' : ''}>Section 2</li>
                <li onClick={() => handleScrollToSection('section3')}
                    className={activeSection === 'section3' ? 'active' : ''}>Section 3</li>
                <li onClick={() => handleScrollToSection('section4')}
                    className={activeSection === 'section4' ? 'active' : ''}>Section 4</li>
            </ul>
            <div>
                <section ref={el => sectionRefs.current['section1'] = el} id="section1">Section 1 Content</section>
                <section ref={el => sectionRefs.current['section2'] = el} id="section2">Section 2 Content</section>
                <section ref={el => sectionRefs.current['section3'] = el} id="section3">Section 3 Content</section>
                <section ref={el => sectionRefs.current['section4'] = el} id="section4">Section 4 Content</section>
            </div>
        </div>
    );
};

export default NextviewportMenu;
