// src/ImageAlbum.js
import React, { useState } from 'react';
import './ImageAlbum.css'; // Ensure this CSS file is created

// Example image imports
import img1 from './../../photos/white_hoodie_mockup.jpg';
import img2 from './../../photos/soft-blurred-blue-painting-abstract-background_120819-132.jpg';
import img3 from './../../photos/portrait-young-adult-wearing-hoodie-mockup_23-2149296253.jpg';
import img4 from './../../photos/coat.jpg';

const ImageAlbum = () => {
  const images = [img1, img2, img3, img4]; // Add more images as needed
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevImage = () => {
    setCurrentIndex((Index) => (Index === 0 ? images.length - 1 : Index - 1));
  };

  const nextImage = () => {
    setCurrentIndex((Index) => (Index === images.length - 1 ? 0 : Index + 1));
  };

  return (
    <div className="image-album-container">
      <button onClick={prevImage} className="nav-button prev-button">&lt;</button>
      <div className="image-album">
        <div className="image-wrapper" style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
          {images.map((image, index) => (
            <img key={index} src={image} alt={`Album ${index}`} />
          ))}
        </div>
      </div>
      <button onClick={nextImage} className="nav-button next-button">&gt;</button>
    </div>
  );
};

export default ImageAlbum;
