import React, { useState, useEffect } from 'react';
import './ImageAlbum.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';
import img1 from './../../photos/white_hoodie_mockup.jpg';
import img2 from './../../photos/soft-blurred-blue-painting-abstract-background_120819-132.jpg';
import img3 from './../../photos/portrait-young-adult-wearing-hoodie-mockup_23-2149296253.jpg';
import img4 from './../../photos/coat.jpg';

const ImageAlbum = () => {
  const images = [img1, img2, img3, img4];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [transitionDirection, setTransitionDirection] = useState('');

  useEffect(() => {
    if (transitionDirection !== '') {
      const timeout = setTimeout(() => setTransitionDirection(''), 500); // Match the CSS transition duration
      return () => clearTimeout(timeout);
    }
  }, [transitionDirection]);

  const prevImage = () => {
    setTransitionDirection('prev');
    setTimeout(() => {
      setCurrentIndex((Index) => (Index === 0 ? images.length - 1 : Index - 1));
    }, 500); // Match the CSS transition duration
  };

  const nextImage = () => {
    setTransitionDirection('next');
    setTimeout(() => {
      setCurrentIndex((Index) => (Index === images.length - 1 ? 0 : Index + 1));
    }, 500); // Match the CSS transition duration
  };

  const getPrevIndex = () => (currentIndex === 0 ? images.length - 1 : currentIndex - 1);
  const getNextIndex = () => (currentIndex === images.length - 1 ? 0 : currentIndex + 1);

  return (
    <div className="image-album-container">
      <button onClick={prevImage} className="nav-button prev-button">
        <FontAwesomeIcon icon={faArrowLeft} />
      </button>
      <div className={`image-album ${transitionDirection}`}>
        <img className="image" src={images[getPrevIndex()]} alt="Previous" style={{ transform: 'translateX(-100%)' }} />
        <img className="image current" src={images[currentIndex]} alt="Current" style={{ transform: 'translateX(0)' }} />
        <img className="image" src={images[getNextIndex()]} alt="Next" style={{ transform: 'translateX(100%)' }} />
      </div>
      <button onClick={nextImage} className="nav-button next-button">
        <FontAwesomeIcon icon={faArrowRight} />
      </button>
    </div>
  );
};

export default ImageAlbum;
