import React, { useState, useEffect } from 'react';
import './NextviewportMenu.css';

const NextviewportMenu = () => {
    const [activeSection, setActiveSection] = useState('section1');

    const handleScrollToSection = (sectionId) => {
        document.getElementById(sectionId).scrollIntoView({ behavior: 'smooth' });
        setActiveSection(sectionId);
    };

    const handleScroll = () => {
        const sections = document.querySelectorAll('section');
        let currentSectionId = activeSection;

        sections.forEach(section => {
            const rect = section.getBoundingClientRect();
            if (rect.top <= window.innerHeight / 2 && rect.bottom >= window.innerHeight / 2) {
                currentSectionId = section.id;
            }
        });

        setActiveSection(currentSectionId);
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [activeSection]);

    return (
        <ul className="next-viewport-menu">
            <li onClick={() => handleScrollToSection('section1')}
                className={activeSection === 'section1' ? 'active' : ''}>Section 1</li>
            <li onClick={() => handleScrollToSection('section2')}
                className={activeSection === 'section2' ? 'active' : ''}>Section 2</li>
            <li onClick={() => handleScrollToSection('section3')}
                className={activeSection === 'section3' ? 'active' : ''}>Section 3</li>
            <li onClick={() => handleScrollToSection('section4')}
                className={activeSection === 'section4' ? 'active' : ''}>Section 4</li>
            <li onClick={() => handleScrollToSection('section5')}
                className={activeSection === 'section5' ? 'active' : ''}>Section 5</li>
            <li onClick={() => handleScrollToSection('section6')}
                className={activeSection === 'section6' ? 'active' : ''}>Section 6</li>
        </ul>
    );
};

export default NextviewportMenu;
