// src/ImageAlbum.js
import React, { useState } from 'react';
import './ImageAlbum.css'; // We will create this CSS file next

// Example image imports
import img1 from './../../photos/white_hoodie_mockup.jpg';
import img2 from './../../photos/yellow-jacket-warm-sweater-hat-white-background-set-children-s-clothes-autumn-winter-fashion-kids-outfit_479776-7715.jpg';
import img3 from './../../photos/coat.jpg';
import img4 from './../../photos/portrait-young-adult-wearing-hoodie-mockup_23-2149296253.jpg';

const ImageAlbum = () => {
  const images = [img1, img2, img3, img4]; // Add more images as needed
  const [currentIndex, setCurrentIndex] = useState(0);
  const [direction, setDirection] = useState(null); // 'next' or 'prev'

  const prevImage = () => {
    setDirection('prev');
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
      setDirection(null);
    }, 500); // Match the duration of the CSS animation
  };

  const nextImage = () => {
    setDirection('next');
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
      setDirection(null);
    }, 500); // Match the duration of the CSS animation
  };

  return (
    <div className="image-album-container">
      <button onClick={prevImage} className="nav-button prev-button">&lt;</button>
      <div className={`image-album ${direction}`}>
        <img src={images[currentIndex]} alt={`Album ${currentIndex}`} />
      </div>
      <button onClick={nextImage} className="nav-button next-button">&gt;</button>
    </div>
  );
};

export default ImageAlbum;
