import React from 'react';
import { useState } from 'react';
import './NextviewportMenu.css';

const NextviewportMenu = () => {
    
    const [activeSection, setActiveSection] = useState(null);

    const handleScroll = (sectionId) => {

        document.getElementById(sectionId).scrollIntoView({ behavior: "smooth" });
    };
    
    return (
        <ul className="next-viewport-menu">
            <li onClick={() => handleScroll('section1')}
                 className={activeSection === 'section1' ? 'active' : ''}></li>
            <li onClick={() => handleScroll('section2')}
                 className={activeSection === 'section2' ? 'active' : ''}></li>
            <li onClick={() => handleScroll('section3')}
                 className={activeSection === 'section3' ? 'active' : ''}></li>
            <li onClick={() => handleScroll('section4')}
                 className={activeSection === 'section4' ? 'active' : ''}></li>
            <li onClick={() => handleScroll('section5')}
                 className={activeSection === 'section5' ? 'active' : ''}></li>
            <li onClick={() => handleScroll('section6')}
                 className={activeSection === 'section6' ? 'active' : ''}></li>
        </ul>
    );
};

export default NextviewportMenu;
