import React from "react";
import './NextviewportMenu.css';

const NextviewportMenu = () => {
    
    const handleScroll = (sectionId) => {
        document.getElementById(sectionId).scrollIntoView({ behavior: "smooth" });
    };
    
    return (
        <ul className="next-viewport-menu">
            <li onClick={() => handleScroll('section1')}>Section 1</li>
            <li onClick={() => handleScroll('section2')}>Section 2</li>
            <li onClick={() => handleScroll('section3')}>Section 3</li>
            <li onClick={() => handleScroll('section4')}>Section 4</li>
            <li onClick={() => handleScroll('section5')}>Section 5</li>
            <li onClick={() => handleScroll('section6')}>Section 6</li>
        </ul>
    );
};

export default NextviewportMenu;
