import React, { useState } from 'react';
import './ImageAlbum.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft,faArrowRight } from '@fortawesome/free-solid-svg-icons';
import img1 from './../../photos/white_hoodie_mockup.jpg';
import img2 from './../../photos/soft-blurred-blue-painting-abstract-background_120819-132.jpg';
import img3 from './../../photos/portrait-young-adult-wearing-hoodie-mockup_23-2149296253.jpg';
import img4 from './../../photos/coat.jpg';

const ImageAlbum = () => {
  const images = [img1, img2, img3, img4]; 
  const [ currentIndex , setCurrentIndex] = useState(0);
  const current =document.getElementsByClassName('currentimage');
  const prev = document.getElementsByClassName('previmage');
  const next = document.getElementsByClassName('nextimage');
  
  

  const prevImage = () => {
    setCurrentIndex((Index) => (Index === 0 ? images.length - 1 : Index-1));
    current.style.transform ='translateX(320px)';
    next.style.transform = 'translateX(-640px)';
    prev.style.transform = 'translateX(320px)';
    current.classList.add('nextimage');
    current.classList.remove('currentimage');
    prev.classList.add('currentimage');
    prev.classList.remove('previmage');
    next.classList.add('previmage');
    next.classList.remove('nextimage');
    };
    
    const nextImage = () => {
      setCurrentIndex((Index) => (Index === images.length - 1 ? 0 : Index+1));
      current.style.transform ='translateX(-320px)';
      next.style.transform = 'translateX(-320px)';
      prev.style.transform = 'translateX(640px)';
      current.id = "prev";
      next.id = "current";
      prev.id = "next";
      };
  const getPrevIndex = () => (currentIndex === 0 ? images.length - 1 : currentIndex - 1);
  const getNextIndex = () => (currentIndex === images.length - 1 ? 0 : currentIndex + 1);
  return (
    <div className="ImageAlbum">
      <button onClick={nextImage} className='prev'><FontAwesomeIcon icon={faArrowLeft}/></button>
      <img id='prev' className='previmage' src={images[getPrevIndex()]} alt=''/>
      <img id='current' className='currentimage' src={images[currentIndex]} alt=''/>
      <img id='next' className='nextimage' src={images[getNextIndex()]} alt=''/>
      <button onClick={prevImage} className='next'><FontAwesomeIcon icon={faArrowRight}/></button>
    </div>
  );
};

export default ImageAlbum;
