import React, { useRef } from "react";
import './NextviewportMenu.css';



const NextviewportMenu = () =>{
    const sectionRefs = {
        section1:document.getElementById('fashioninformationbox'),
        section2: useRef(null),
        section3: useRef(null),
        section4: useRef(null),
        section5: useRef(null),
        section6: useRef(null)
      };
      const handleScroll = (section) => {
        sectionRefs[section].current.scrollIntoView({ behavior: 'smooth' });
      }
  
    return(
        <ul className="next-viewport-menu">
            <li onClick={() => handleScroll('section1')}></li>
            <li onClick={() => handleScroll('section2')}></li>
            <li onClick={() => handleScroll('section3')}></li>
            <li onClick={() => handleScroll('section4')}></li>
            <li onClick={() => handleScroll('section5')}></li>
            <li onClick={() => handleScroll('section6')}></li>
        </ul>
    );
}
export default NextviewportMenu
