import React, { useState } from 'react';
import './ImageAlbum.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';
import img1 from './../../photos/white_hoodie_mockup.jpg';
import img2 from './../../photos/soft-blurred-blue-painting-abstract-background_120819-132.jpg';
import img3 from './../../photos/portrait-young-adult-wearing-hoodie-mockup_23-2149296253.jpg';
import img4 from './../../photos/coat.jpg';

const ImageAlbum = () => {
  const images = [img1, img2, img3, img4];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [direction, setDirection] = useState('');

  const prevImage = () => {
    if (isAnimating) return;
    setIsAnimating(true);
    setDirection('prev');
    setTimeout(() => {
      setCurrentIndex((index) => (index === 0 ? images.length - 1 : index - 1));
      setIsAnimating(false);
    }, 300);
  };

  const nextImage = () => {
    if (isAnimating) return;
    setIsAnimating(true);
    setDirection('next');
    setTimeout(() => {
      setCurrentIndex((index) => (index === images.length - 1 ? 0 : index + 1));
      setIsAnimating(false);
    }, 300);
  };

  const getPrevIndex = () => (currentIndex === 0 ? images.length - 1 : currentIndex - 1);
  const getNextIndex = () => (currentIndex === images.length - 1 ? 0 : currentIndex + 1);

  return (
    <div className="ImageAlbum">
      <button onClick={prevImage} className='prev'>
        <FontAwesomeIcon icon={faArrowLeft} />
      </button>
      <div className={`image-container ${isAnimating ? `animating ${direction}` : ''}`}>
        <img id='prev' src={images[getPrevIndex()]} alt='' className='prev-img'/>
        <img id='current' src={images[currentIndex]} alt='' className='current-img'/>
        <img id='next' src={images[getNextIndex()]} alt='' className='next-img'/>
      </div>
      <button onClick={nextImage} className='next'>
        <FontAwesomeIcon icon={faArrowRight} />
      </button>
    </div>
  );
};

export default ImageAlbum;
