        section1.scrollIntoView({behavior:"smooth"})
    }
    
    return(
        <ul className="next-viewport-menu">
            <li onClick={handleClick}></li>
            <li onClick={() => handleScroll('section2')}></li>
            <li onClick={() => handleScroll('section3')}></li>
            <li onClick={() => handleScroll('section4')}></li>
            <li onClick={() => handleScroll('section5')}></li>
            <li onClick={() => handleScroll('section6')}></li>
        </ul>
    );
}
