import React, { useState } from 'react';
import './ImageAlbum.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft,faArrowRight } from '@fortawesome/free-solid-svg-icons';
import img1 from './../../photos/white_hoodie_mockup.jpg';
import img2 from './../../photos/soft-blurred-blue-painting-abstract-background_120819-132.jpg';
import img3 from './../../photos/portrait-young-adult-wearing-hoodie-mockup_23-2149296253.jpg';
import img4 from './../../photos/coat.jpg';

const ImageAlbum = () => {
  const images = [img1, img2, img3, img4]; // Add more images as needed
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevImage = () => {
    setCurrentIndex((Index) => (Index === 0 ? images.length - 1 : Index - 1));
  };

  const nextImage = () => {
    setCurrentIndex((Index) => (Index === images.length - 1 ? 0 : Index + 1));
  };

  return (
    <div className="ImageAlbum">
      <button onClick={prevImage} className='prev'><FontAwesomeIcon icon={faArrowLeft}/></button>
      <img className='previmage' src={images[currentIndex-1]} alt=''/>
      <img className='currentimage' src={images[currentIndex]} alt=''/>
      <img className='nextimage' src={images[currentIndex+1]} alt=''/>
      <button onClick={nextImage} className='next'><FontAwesomeIcon icon={faArrowRight}/></button>
    </div>
  );
};

export default ImageAlbum;
